import React from 'react'
import { Avatar, Button, Card, Title, Text } from 'react-native-paper';

import { View, StyleSheet, Image } from 'react-native';

const NoteCard = (props) => {
    return (
        <Card style={styles.shadow}>
            <View style={styles.user}>
            <Card.Content style={{ flex: 1, flexDirection: "column" }}>
                    <Text style={styles.title}> {props.currentNote.title}</Text>
                    <Text style={{ fontSize: 16, color: 'black' }}> {props.currentNote.body}</Text>
                    
                </Card.Content>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        marginBottom: 8,
        height: 130,
        width: 350,
        borderRadius: 15
    },
    user: {
        flexDirection: 'row',
        marginTop: 8,
        marginLeft: 8,
    },
    title: {
        fontWeight: "bold",
        fontSize: 30,
        color: "#fb5b5a",
     
    },
})

export default NoteCard;
