import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Image, Alert, AsyncStorage } from 'react-native';
import fire from '../fire'

const LoginScreen = ({ navigation }) => {

    const [email, setEmail] = useState({ value: '' })
    const [password, setPassword] = useState({ value: '' })
    var user = null;

    useEffect(() => {
        getLocalUser()
    }, [])

    const storeLocalUser = async () => {

        try {
            await AsyncStorage.setItem('@email', email.value)
            await AsyncStorage.setItem('@password', password.value)
        } catch (e) {
            console.log(e)
        }
    }

    const getLocalUser = async () => {
        try {
            const localEmail = await AsyncStorage.getItem('@email')
            const localPassword = await AsyncStorage.getItem('@password')
           
        } catch (e) {
            // error reading value
        }

        if (true) {
            setEmail({ value: localEmail })
            setPassword({ value: localPassword })
            console.log(email)
            console.log(password)
            loginBtn();
        }

    }


    /*   useEffect(() => {
          autoLogin();
        }, [autoLogin])
  
        function autoLogin(){
          if (user!=null) {
              alert(user.email)
          }
          else{
              alert("else")
          }
        }
         */

    const loginBtn = () => {
        console.log(email.value)
        console.log(password.value)
        fire.auth()
            .signInWithEmailAndPassword(email.value, password.value)
            .then(() => {
                fire.auth().onAuthStateChanged(authUser => {
                    if (authUser !== null) {
                        user = authUser;
                        console.log(authUser)
                        Alert.alert(
                            'Welcome',
                            'Hello ' + authUser.email,
                            [
                                { text: 'Ok' },

                            ],
                            { cancelable: false },
                        );
                        storeLocalUser(email.value, password.value);
                        navigation.navigate('HomeScreen', {
                            currentUser: authUser,
                        });


                    }
                })
            })
            .catch(err => {
                switch (err.code) {
                    case "auth/invalid-email":
                    case "auth/user-disabled":
                    case "auth/user-not-found":
                        alert("wrong Email");
                        console.log(err.message)
                        break;
                    case "auth/wrong-password":
                        alert("wrong Password");
                        console.log(err.message)
                        break;
                }

            });
    }


    return (
        <View style={styles.container}>

            <Image
                style={styles.logo}
                source={require('../assets/moveoappLogo.png')}
            />
            <Text style={styles.headLine}>Welcome to MoveoNote</Text>
            <View style={styles.inputView} >
                <TextInput
                    style={styles.inputText}
                    placeholder="Email..."
                    placeholderTextColor="#003f5c"
                    onChangeText={(text) => setEmail({ value: text })} />
            </View>
            <View style={styles.inputView} >
                <TextInput
                    secureTextEntry
                    style={styles.inputText}
                    placeholder="Password..."
                    placeholderTextColor="#003f5c"
                    onChangeText={(text) => setPassword({ value: text })} />
            </View>
            <TouchableOpacity onPress={loginBtn} style={styles.loginBtn}>
                <Text style={styles.loginText}>LOGIN</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('RegisterScreen')}>
                <Text style={styles.loginText}>Signup</Text>
            </TouchableOpacity>
        </View>

    )

}

export default LoginScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'rgb(241, 241, 241)',
        alignItems: 'center',
        justifyContent: 'center',

    },
    headLine: {
        fontWeight: "bold",
        fontSize: 25,
        position: 'absolute',
        top: 65,
        color: "#808085",
    },
    logo: {
        width: "50%",
        height: "20%",
        marginBottom: 40
    },

    appName: {
        fontWeight: "bold",
        fontSize: 40,
        color: "#fb5b5a",
        marginBottom: 40
    },
    inputView: {
        width: "80%",
        backgroundColor: "white",
        borderRadius: 25,
        height: 50,
        marginBottom: 20,
        justifyContent: "center",
        padding: 20,
    },
    inputText: {
        height: 70,

    },
    loginBtn: {
        width: "80%",
        backgroundColor: "rgb(241 ,180, 23)",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        marginBottom: 10

    },
    loginText: {
        color: "#808080",
        fontWeight: "bold",
        fontSize: 15,
    }
});
