import React from 'react'
import { Avatar, Button, Card, Title, Text } from 'react-native-paper';
import { FontAwesome,MaterialIcons  } from '@expo/vector-icons'
import { View, StyleSheet, Image } from 'react-native';

const NoteCard = (props) => {
    return (
        <Card style={styles.shadow}>
            <View style={styles.user}>
            <Card.Content style={{ flex: 1, flexDirection: "column" }}>
                    <Text style={styles.title}>{props.currentNote.title}</Text>
                    <Text style={styles.body}>{props.currentNote.body}</Text>
                    <FontAwesome style={styles.edit} name="edit" size={32} color="rgb(50, 191, 104)" />
                    <MaterialIcons style={styles.delete} name="delete-outline" size={36} color="rgb(191, 50, 76)" />
                </Card.Content>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        marginBottom: 8,
        height: 130,
        width: 350,
        borderRadius: 15
    },
    user: {
        flexDirection: 'row',
        marginTop: 8,
        marginLeft: 8,
    },
    title: {
        fontWeight: "bold",
        fontSize: 20,
        color:  "rgb(241 ,180, 23)",
        marginBottom:5
     
    },
    body: {
        fontSize: 15,
        color: "black",
     
    },
    edit:{
        position:'relative',
        left:"90%",
        bottom:"40%"
    },
    delete:{
        position:'relative',
        left:"89%",
        bottom:"20%"
    }
})

export default NoteCard;
