import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Image, Alert, AsyncStorage, KeyboardAvoidingView } from 'react-native';
import fire from '../fire'

const LoginScreen = ({ navigation }) => {

    const [email, setEmail] = useState({ value: '' })
    const [password, setPassword] = useState({ value: '' })
    var user = null;

    useEffect(() => {
        getLocalUser()
    }, [])

    const storeLocalUser = async (e, p) => {
        try {
            await AsyncStorage.setItem('@email', e)
            await AsyncStorage.setItem('@password', p)
        } catch (e) {
            console.log(e)
        }
    }

    const getLocalUser = async () => {
        try {
            let localEmail = await AsyncStorage.getItem('@email')
            let localPassword = await AsyncStorage.getItem('@password')

            if (localEmail != "null" && localPassword != "null") {
                loginBtn(localEmail, localPassword)
            }
        }
        catch (e) {
            console.log(e)
        }
    }

    const loginBtn = async (e, p) => {
        console.log(e + " " + p)
        await fire.auth()
            .signInWithEmailAndPassword(e, p)
            .then(() => {
                fire.auth().onAuthStateChanged(authUser => {
                    if (authUser !== null) {
                        user = authUser;
                        console.log(authUser)
                        Alert.alert(
                            'Hello ' + authUser.email+" ðŸ‘‹",
                            'Were really happy to see you ðŸ¥³',
                            [
                                { text: 'Ok' },
                            ],
                            { cancelable: false },
                        );
                        storeLocalUser(email.value, password.value);
                        navigation.navigate('HomeScreen', {
                            currentUser: authUser,
                        });
                    }
                })
            })
            .catch(err => {
                switch (err.code) {
                    case "auth/invalid-email":
                    case "auth/user-disabled":
                    case "auth/user-not-found":
                        alert("wrong Email");
                        console.log(err.message)
                        break;
                    case "auth/wrong-password":
                        alert("wrong Password");
                        console.log(err.message)
                        break;
                }

            });
    }


    return (
       
            <View style={styles.container}>
                <Image 
                    style={styles.logo}
                    source={require('../assets/moveoappLogo.png')}
                />
                <Text style={styles.headLine}>Welcome to MoveoNote</Text>
                <View style={styles.inputView1} >
                    <TextInput
                        style={styles.inputText}
                        placeholder="Email..."
                        placeholderTextColor="#003f5c"
                        onChangeText={(text) => setEmail({ value: text })} />
                </View>
                <View style={styles.inputView2} >
                    <TextInput
                        secureTextEntry
                        style={styles.inputText}
                        placeholder="Password..."
                        placeholderTextColor="#003f5c"
                        onChangeText={(text) => setPassword({ value: text })} />
                </View>
                <TouchableOpacity onPress={() => { loginBtn(email.value, password.value) }}
                    style={styles.loginBtn}>
                    <Text style={styles.loginText}>LOGIN</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.signup} onPress={() => navigation.navigate('RegisterScreen')}>
                    <Text style={styles.loginText}>Signup</Text>
                </TouchableOpacity>
            </View>
       

    )

}

export default LoginScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'rgb(241, 241, 241)',
        alignItems: 'center',
        justifyContent: 'center',

    },
    headLine: {
        fontWeight: "bold",
        fontSize: 25,
        position: 'absolute',
        top: 65,
        color: "#808085",
    },
    logo: {
        width: 200,
        height: 200,
        position: 'absolute',
        top: 120,
    },

    appName: {
        fontWeight: "bold",
        fontSize: 40,
        color: "#fb5b5a",
        marginBottom: 40
    },
    inputView1: {
        width: "80%",
        backgroundColor: "white",
        borderRadius: 25,
        height: 50,
        justifyContent: "center",
        padding: 20,
        position: 'absolute',
        top: 400,
    },
    inputView2: {
        width: "80%",
        backgroundColor: "white",
        borderRadius: 25,
        height: 50,
        justifyContent: "center",
        padding: 20,
        position: 'absolute',
        top: 470,
    },
    inputText: {
        height: 70,

    },
    loginBtn: {
        width: "80%",
        backgroundColor: "rgb(241 ,180, 23)",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        position: 'absolute',
        top: 650   
    },
    loginText: {
        color: "#808080",
        fontWeight: "bold",
        fontSize: 15,
    },
    signup: {
        position: 'absolute',
        top: 720,   
    }
});
